import axios from "axios";
import React, { Fragment, useEffect, useState } from "react";

import "./Local.css";

const Local = () => {
  const apiKey = "749d030bb028567c6df26b3941472ec9";

  const [data, setData] = useState("");

  const setWeatherDetails = (nomeCidade) => {
    if (!nomeCidade) return;
    axios
      .get(
        `http://api.openweathermap.org/data/2.5/weather?q=${nomeCidade}&appid=${apiKey}`
      )
      .then((res) => {
        // console.log("response", res.data);

        setData(res.data);
      })
      .catch((err) => {
        return;
      });
  };

  useEffect(() => {
    setWeatherDetails("Ipatinga");
  }, []);

  return (
    <Fragment>
      <div className="lugar text-center">
        <h2 className="cidade">{data?.name}</h2>

        <h1 className="temperatura">
          {(data?.main?.temp - 273.15).toFixed(2)}ºC
        </h1>
      </div>
    </Fragment>
  );
};

export default Local;
